import './ExpenseForm.css'

const ExpenseForm = () => {
   const [enteredTitle, setEnterTitle] =  useState('');
   const [enteredAmount, setEnterAmount] = useState('');
   const [enteredDate, setEnteredDate] = useState('');

   const titleChangeHandler = (event) => {
        setEnterTitle(event.target.value);   
   }
   const amountChangeHandler = (event) => {
        setEnterAmount(event.target.value);
    }

    const dateChangeHandler = () =>{
        setEnteredDate(event.target.value);
    }


  return (
        <form>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type="text" onChange={titleChangeHandler}/>
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input type="number" min="0.01" step="0.01" onChange={titleChangeHandler}/>
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type="date" min="2019-01-01" max = "2022-12-31" onChange={titleChangeHandler}/>
                </div>
                <div className="new-xepense__action">
                <button  type="submit">Add Expenses</button>
                </div>
            </div>    
        </form>
  )
}

export default ExpenseForm
